import {
  FETCH_PHONES_START,
  FETCH_PHONES_SUCCESS,
  FETCH_PHONES_FAILURE,
  LOAD_MORE_START,
  LOAD_MORE_SUCCESS,
  LOAD_MORE_FAILURE,
  FETCH_PHONE_BY_ID_START,
  FETCH_PHONE_BY_ID_SUCCESS,
  FETCH_PHONE_BY_ID_FAILURE,
  ADD_PHONE_TO_BASKET,
  SEARCH_PHONES,
  FETCH_CATEGORIES_START,
  FETCH_CATEGORIES_SUCCESS,
  FETCH_CATEGORIES_FAILURE,
  REMOVE_PHONE_FROM_BASKET,
  CLEAR_BASKET
} from 'constants/actionTypes'

import {
  fetchPhones as fetchPhonesApi,
  loadMorePhones as loadMorePhonesApi,
  fetchPhoneApi,
  fetchCategories as fetchCategoriesApi
} from 'api'
import {getRenderedPhoneLength} from 'selectors'

export const fetchPhones = () => async dispatch => {
  dispatch({
    type: FETCH_PHONES_START
  })
  try {
    const phones = await fetchPhonesApi()
    dispatch({
      type: FETCH_PHONES_SUCCESS,
      payload: phones
    })
  } catch (error) {
    dispatch({
      type: FETCH_PHONES_FAILURE,
      payload: error,
      error: true
    })
    console.log(error)
  }
}

export const loadMorePhones = () => async (dispatch, getState) => {
  const offset = getRenderedPhoneLength(getState())
  dispatch({
    type: LOAD_MORE_START
  })
  try {
    const phones = await loadMorePhonesApi({offset})
    dispatch({
      type: LOAD_MORE_SUCCESS,
      payload: phones
    })
  } catch (error) {
    dispatch({
      type: LOAD_MORE_FAILURE,
      payload: error,
      error: true
    })
  }
}

export const fetchPhoneById = id => async dispatch => {
  dispatch({
    type: FETCH_PHONE_BY_ID_START
  })
  try {
    const phone = await fetchPhoneApi(id)
    console.log(phone)
    dispatch({
      type: FETCH_PHONE_BY_ID_SUCCESS,
      payload: phone
    })
  } catch (err) {
    dispatch({
      type: FETCH_PHONE_BY_ID_FAILURE,
      payload: err,
      error: true
    })
  }
}

export const addPhoneToBasket = id => dispatch => {
  dispatch({
    type: ADD_PHONE_TO_BASKET,
    payload: id
  })
}

export const searchPhones = text => dispatch => {
  dispatch({
    type: SEARCH_PHONES,
    payload: text
  })
}
export const removePhoneFromBasket = id => dispatch => {
  dispatch({
    type: REMOVE_PHONE_FROM_BASKET,
    payload: id
  })
}
export const clearBasket = () => dispatch => {
  dispatch({
    type: CLEAR_BASKET
  })
}

export const fetchCategories = () => async dispatch => {
  dispatch({
    type: FETCH_CATEGORIES_START
  })
  try {
    const categories = await fetchCategoriesApi()
    dispatch({
      type: FETCH_CATEGORIES_SUCCESS,
      payload: categories
    })
  } catch (error) {
    dispatch({
      type: FETCH_CATEGORIES_FAILURE,
      payload: error,
      error: true
    })
    console.log(error)
  }
}
