import * as R from 'ramda'
import React from 'react'
import {connect} from 'react-redux'
import {compose} from 'redux'
import {withRouter} from 'react-router'
import {Link} from 'react-router-dom'
import {getCategories, getActiveCategoryId} from 'selectors'
import classNames from 'classnames'

const Categories = ({categories, activeCaregoryId}) => {
  const renderCategories = (category, i) => {
    const linkClass = classNames({
      'list-group-item': true,
      active: activeCaregoryId === category.id
    })
    return (
      <Link to={`/categories/${category.id}`} className={linkClass} key={i}>
        {category.name}
      </Link>
    )
  }
  const allCategories = () => {
    const linkClass = classNames({
      'list-group-item': true,
      active: R.isNil(activeCaregoryId)
    })
    return (
      <Link to="/" className={linkClass}>
        All
      </Link>
    )
  }
  return (
    <div className="well">
      <h4>Brand</h4>
      <div className="list-group">
        {allCategories()}
        {categories.map((category, i) => {
          return renderCategories(category, i)
        })}
      </div>
    </div>
  )
}
const mapStateToProps = (state, ownProps) => ({
  categories: getCategories(state),
  activeCaregoryId: getActiveCategoryId(ownProps)
})
export default compose(
  withRouter,
  connect(
    mapStateToProps,
    null
  )
)(Categories)
