import React, {Component} from 'react'
import {connect} from 'react-redux'
import * as R from 'ramda'
import {fetchPhoneById} from 'actions'
import {getPhoneById} from 'selectors'

class Phone extends Component {
  componentWillMount() {
    this.props.fetchPhoneById(this.props.match.params.id)
  }
  transformPhone = () => {
    const {phone} = this.props
    const columnFields = R.compose(
      R.toPairs,
      R.pick([
        'cpu',
        'camera',
        'size',
        'weight',
        'display',
        'battery',
        'memory'
      ])
    )(phone)
    return columnFields.map(([elem, i]) => (
      <div className="column" key={i}>
        <div className="ab-details-title">
          <p>{i}</p>
        </div>
        <div className="ab-details-info">
          <p>{elem}</p>
        </div>
      </div>
    ))
  }
  renderContent = () => {
    const {phone} = this.props
    console.log(phone)
    return (
      <div className="thumbnail">
        <div className="row">
          <div className="col-md-6">
            <img src={phone.image} className="img-thumbnail" alt={phone.name} />
          </div>
          <div className="col-md-6">
            Ourpairs
            {this.transformPhone()}
          </div>
          <div className="caption-full">
            <h4 className="pull-right">${phone.price}</h4>
            <h4>{phone.name}</h4>
            <p>{phone.description}</p>
          </div>
        </div>
      </div>
    )
  }
  renderSidebar = () => {
    return <div className="first">Sidebar</div>
  }

  render() {
    const {phone} = this.props
    return (
      <div className="view-container">
        <div className="container">
          <div className="row">
            <div className="col-md-9">{phone && this.renderContent()}</div>
            <div className="col-md-3">{phone && this.renderSidebar()}</div>
          </div>
        </div>
      </div>
    )
  }
}

const mapStateToProps = state => {
  return {
    phone: getPhoneById(state, state.phonePage.id)
  }
}
const mapDispatchToProps = {
  fetchPhoneById
}
export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Phone)
