import React, {Component} from 'react'
import {connect} from 'react-redux'
import * as R from 'ramda'
import {fetchPhoneById, addPhoneToBasket} from 'actions'
import {getPhoneById} from 'selectors'
import Basket from 'components/Basket'
import {Link} from 'react-router-dom'
class Phone extends Component {
  componentWillMount() {
    this.props.fetchPhoneById(this.props.match.params.id)
  }
  transformPhone = () => {
    const {phone} = this.props
    const columnFields = R.compose(
      R.toPairs,
      R.pick([
        'cpu',
        'camera',
        'size',
        'weight',
        'display',
        'battery',
        'memory'
      ])
    )(phone)
    return columnFields.map(([elem, i]) => (
      <div className="column" key={i}>
        <div className="ab-details-info col-md-6">
          <p>{elem}</p>
        </div>
        <div className="ab-details-title col-md-6">
          <p>{i}</p>
        </div>
      </div>
    ))
  }
  renderContent = () => {
    const {phone} = this.props
    // console.log(phone)
    return (
      <div className="thumbnail">
        <div className="row">
          <div className="col-md-6">
            <img src={phone.image} className="thumbnail-img" alt={phone.name} />
          </div>
          <div className="col-md-6">
            Details
            {this.transformPhone()}
          </div>
          <div className="thumbnail-caption-full">
            <h4 className="pull-right">${phone.price}</h4>
            <h4 className="name">{phone.name}</h4>
            <p>{phone.description}</p>
          </div>
        </div>
      </div>
    )
  }
  renderSidebar = () => {
    const {phone} = this.props
    return (
      <div>
        <p className="lead">Quick shop</p>
        <Basket />
        <div className="form-group">
          <h1>{phone.name}</h1>
          <h2>{phone.price}</h2>
        </div>
        <Link to="/" className="btn btn-info btn-block">
          Back to store
        </Link>
        <button
          className="btn btn-success btn-block"
          onClick={() => this.props.addPhoneToBasket(phone.id)}
        >
          Add to cart
        </button>
      </div>
    )
  }

  render() {
    const {phone} = this.props
    return (
      <div className="view-container">
        <div className="container">
          <div className="row">
            <div className="col-md-9">{phone && this.renderContent()}</div>
            <div className="col-md-3">{phone && this.renderSidebar()}</div>
          </div>
        </div>
      </div>
    )
  }
}

const mapStateToProps = state => {
  return {
    phone: getPhoneById(state, state.phonePage.id)
  }
}
const mapDispatchToProps = {
  fetchPhoneById,
  addPhoneToBasket
}
export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Phone)
