import React, {Component} from 'react'
import {connect} from 'react-redux'
import * as R from 'ramda'
import {Link} from 'react-router-dom'
import {fetchPhones, loadMorePhones, addPhoneToBasket, fetchCategories} from 'actions'
import {getPhones} from 'selectors'
import Layout from 'containers/layout';
import Search from 'components/Search'
class Phones extends Component {
  componentDidMount() {
    this.props.fetchPhones();
    this.props.fetchCategories()
  }
  renderPhone = (phone, i) => {
    const {addPhoneToBasket} = this.props
    const trimDesc = `${R.take(60, phone.description)}...`
    return (
      <div key={i} className="col-sm-4 col-lg-4 col-md-4 book-list">
        <div className="thumbnail">
          <img className="img-thumbnail" src={phone.image} alt={phone.name} />
          <div className="caption">
            <h4 className="pull-right">${phone.price}</h4>
            <h4>
              <Link to={`/phones/${phone.id}`}>{phone.name}</Link>
            </h4>
            <p>{trimDesc}</p>
            <p className="itemButton">
              <button
                className="btn btn-primary"
                onClick={() => addPhoneToBasket(phone.id)}
              >
                Buy now!
              </button>
              <Link className="btn btn-default" to={`/phones/${phone.id}`}>
                More info!
              </Link>
            </p>
          </div>
        </div>
      </div>
    )
  }

  render() {
    const {phones} = this.props
    // console.log(phones)
    return (
      <Layout>
        <div className="books row">
          {phones.map((phone, i) => {
            return this.renderPhone(phone, i)
          })}
        </div>
        <div className="row">
          <div className="col-md-12 moreButton">
            <button
              onClick={this.props.loadMorePhones}
              className="btn btn-primary pull-right"
            >
              more...
            </button>
          </div>
        </div>
      </Layout>
    )
  }
}

const mapStateToProps = (state, ownProps) => ({
  phones: getPhones(state, ownProps)
})

const mapDispatchToProps = {
  fetchPhones,
  loadMorePhones,
  addPhoneToBasket,
  fetchCategories
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Phones)
