import {FETCH_PHONE_BY_ID_SUCCESS} from 'constants/actionTypes'
import * as R from 'ramda'
const initialState = {
  id: null
}

export default (state = initialState, action) => {
  switch (action.type) {
    case FETCH_PHONE_BY_ID_SUCCESS:
      return R.merge(state, {
        id: action.payload.id
      })
    default:
      return state
  }
}
